#!/bin/bash
BOLD=$(tput bold)
RESET=$(tput sgr0)

red_color(){
	printf "\033[0;31m%s\033[0m" "$1"
}

# Função para colorir o texto em verde
green_color(){
	printf "\033[0;32m%s\033[0m" "$1"
}

yellow_color(){
	printf "\033[0;33m%s\033[0m" "$1"
}

