	checker_output=$(./push_swap "$ARG" | ./checker_Mac "$ARG")

	if [ "$checker_output" = "OK" ]; then
		green_color "OK"
	else
		red_color "KO"
	fi
	./push_swap "$ARG" | wc -l | tr -d '\n' ; printf " op;   "
